example.code:
public ActionResult MaskedTextBox()
                    {
                        MaskEditTextBoxModel masked = new MaskEditTextBoxModel();
                        renderEvents(masked);
                        masked.Mask = "999-999-999-999"
                        masked.Height = 50;
                        masked.Width = 500;
                        masked.WaterMarkText = "Enter Value";
                        ViewData["masked"] = masked;
                        return View();
                    }

